mapper.update:
update tbl_reply set reply = #{reply},
updatedate = curdate() 
where rno = #{rno}

mapper.select:

select rno, bno, reply, replyer, replyDate, updateDate
from (
    select
    (@R:=@R+1) rn, rno, bno, reply, replyer, replyDate, updateDate
    from tbl_reply, (select @R:=0) s1
    where bno = #{bno} and rno > 0
    and (@R) < #{cri.pageNum} * #{cri.amount}
order by rn desc
) s2
where rn > (#{cri.pageNum} -1) * #{cri.amount}
